with open('{}.csv'.format(sys.argv[1]), newline='') as csvfile:
    data = csv.reader(csvfile, delimiter=',', quotechar='|')
    f = open('{}.csv'.format(sys.argv[2]), 'wb')
    for row in data:
        checked = Checker.spell_check(row[1], 'lolwa')
        f.write(str.encode(row[0]))
        f.write(str.encode(','))
        if checked:
            f.write(str.encode(str(checked)))
            # f.write(checked)
            f.write(str.encode('\n'))
        else:
            f.write(str.encode(row[1]))
            f.write(str.encode('\n'))
